def toppings

  if rand(10) < 5
    return "meatball"
  end

  if 1 < 5
    return 'tomato'
  else
    if 4 ==5
      if 2 > 0
        return "parmesan"
      else
        return "romano"
      end
    else
      if 2 > 0
        return "alfredo"
      else
        return "gravy"
      end
    end
  end

end